import logo from './logo.svg';
import './App.css';
import { BrowserRouter as Router , Route, Switch, Link } from 'react-router-dom';

function App() {

const books = [
  {
    id: 1,
    name: 'Desert Solitaire',
    author: 'Edward Abbey',
    overview: 'No author encapsulated and celebrated the American Southwest more engagingly than iconoclast and raconteur Edward Abbey. Desert Solitaire: A Season in the Wilderness — now nearly a half-century old — is a classic of environmental writing. In this autobiographical work, Abbey chronicles his time as a park ranger and reflects on landscape, culture, politics, tourism, environmental disregard, and degradation — doing so with a unique blend of ornery charm and breathtaking description. Though set in his beloved Southwest, Desert Solitaire beautifully and brashly captures the essence of the American outdoors, replete with disdain for those whod seek to spoil its natural wonder. – Jeremy G.'
  },
  {
    id: 2,
    name: 'White Tiger',
    author: 'Arvind Adiga',
    overview: 'Arvind Adiga’s Man-Booker-Prize-winning debut novel was widely acclaimed for its refreshing take on social class disparities and contradictions in contemporary India. The book is a thrilling first-person narrative told from the perspective of Balram Halwai, a young man from a poverty-stricken small village who moves to Delhi to work as a chauffeur for the elite.'
  }
];

return (
<Router>
    <Switch>
          <Route path="/details">
            <Details />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
    </Router>
  );
}

function Home() {
  const booklist = books.map((book) => (
    <div>
      <h1>{book.name}<Link to="/details"><button>Click</button></Link></h1>

      </div>
      ));

      return (


        <div className="App">
          {booklist}
        </div>
      )
}

function Details() {
  return <h2>About</h2>;
}

export default App;
