export const books = [{
  id: 0,
  name: 'Desert Solitaire',
  author: 'Edward Abbey',
  overview: 'No author encapsulated and celebrated the American Southwest more engagingly than iconoclast and raconteur Edward Abbey. Desert Solitaire: A Season in the Wilderness — now nearly a half-century old — is a classic of environmental writing. In this autobiographical work, Abbey chronicles his time as a park ranger and reflects on landscape, culture, politics, tourism, environmental disregard, and degradation — doing so with a unique blend of ornery charm and breathtaking description. Though set in his beloved Southwest, Desert Solitaire beautifully and brashly captures the essence of the American outdoors, replete with disdain for those whod seek to spoil its natural wonder. – Jeremy G.',
  url: "https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1312008341l/242268.jpg"
}, {
  id: 1,
  name: 'White Tiger',
  author: 'Arvind Adiga',
  overview: 'Arvind Adiga’s Man-Booker-Prize-winning debut novel was widely acclaimed for its refreshing take on social class disparities and contradictions in contemporary India. The book is a thrilling first-person narrative told from the perspective of Balram Halwai, a young man from a poverty-stricken small village who moves to Delhi to work as a chauffeur for the elite.',
  url: "https://m.media-amazon.com/images/I/51+FQCxWV5L.jpg"
}, {
  id: 2,
  name: 'Nectar in a Sieve',
  author: 'Kamala Markandaya',
  overview: 'Acclaimed Indian author Kamala Markandaya’s debut novel, Nectar in a Sieve, is the story of rapidly changing mid-20th-century India, told from the perspective of Rukhmani, a woman from rural and impoverished India. From her arranged marriage to Nathan, a farmer, to the changes brought about by the advent of a large tannery in their village, the novel covers a rich range of subjects with uniquely gripping prose.',
  url: 'https://img.theculturetrip.com/1440x/smart/wp-content/uploads/2017/08/kamala-markandaya-nectar-in-a-sieve-createspace-independent-publishing-platform.jpg'
}, {
  id: 3,
  name: 'Train to Pakistan',
  author: 'Khushwant Singh',
  overview: 'This historical Indian book by Khushwant Singh was widely revered during its release in 1956 for bringing a human perspective to the partition of British India into India and Pakistan. While most accounts of the partition at the time primarily focused on political aspects, Singh recounted the event in terms of human loss and horror. Train to Pakistan is an essential read for anyone looking to explore Indian literature or history.',
  url: 'https://images-na.ssl-images-amazon.com/images/I/915ojTuXD+L.jpg'
}, {
  id: 4,
  name: 'The Great Indian Novel',
  author: 'Shashi Tharoor',
  overview: 'This satirical novel by Shashi Tharoor recreates the Hindu epic Mahabharata within the context of the Indian Independence Movement and its following decades to become one of the most exciting reads in contemporary Indian literature. Recasting figures from India’s freedom struggle and politics as mythological characters from a 2,000-year-old epic, Tharoor’s work is a powerful read regardless of how familiar you are with the country.',
  url: 'https://img.theculturetrip.com/1440x/smart/wp-content/uploads/2017/08/great-indian-novel-shashi-tharoor-viking-press.jpg'
}, {
  id: 5,
  name: 'Palace of Illusions',
  author: 'Chitra Banerjee Divakaruni',
  overview: 'Chitra Banerjee Divakaruni’s award-winning novel retells the Hindu mythological epic Mahabharata from the perspective of Draupadi, its lead female character. Draupadi is famous for having married all five of the Pandava brothers – the protagonists of the Mahabharata – and is an ever present, central character through their journey into exile and war. However, little is told from her perspective or about her motives and thoughts in the original epic, which Divakaruni tactfully reclaims in Palace of Illusions.',
  url: 'https://img.theculturetrip.com/1440x/smart/wp-content/uploads/2017/08/9780330458535.jpg'
}, {
  id: 6,
  name: 'The Guide',
  author: 'R.K. Narayan',
  overview: 'R.K. Narayan is among the most read and celebrated authors in Indian books and literature. Based in the famous fictional town of Malgudi in South India, The Guide follows the story of a Railway Raju, a corrupt tour guide, and the odd sequence of events which go on to make him a spiritual guide and eventually a revered holy man in the country.',
  url: 'https://upload.wikimedia.org/wikipedia/en/thumb/a/a4/TheGuide.jpg/220px-TheGuide.jpg'
}, {
  id: 7,
  name: 'In Custody',
  author: 'Anita Desai',
  overview: 'Anita Desai’s novel, which was shortlisted for the Booker Prize, revolves around Deven Sharma, an Urdu scholar in small town India who is caught in an ordinary, mundane life teaching the language to indifferent college students. When he is given a chance to interview Nur, one of the country’s finest Urdu poets, he sees it as a way to channel his love for the language in a more meaningful way.',
  url: 'https://img.theculturetrip.com/1440x/smart/wp-content/uploads/2017/08/in-custody-anita-desai-rhi.jpg'
}, {
  id: 8,
  name: 'The God of Small Things',
  author: 'Arundhati Roy ',
  overview: 'The Booker-Prize-winning debut novel by Arundhati Roy tells the story of a family in 1960s Kerala. Delving deep into a range of issues from the caste system to the state’s encounters with communism, the story follows two fraternal twins, their parents and their extended family as they navigate life in this highly acclaimed work by the famous activist and writer.',
  url: 'https://images-na.ssl-images-amazon.com/images/I/41jFJcgRRtL._SX326_BO1,204,203,200_.jpg'
}, {
  id: 9,
  name: 'A Fine Balance',
  author: 'Rohinton Mistry',
  overview: 'Set in 1975 against the backdrop of the central government declaring a State of Emergency throughout India, Rohinton Mistry’s literary masterpiece tells the story of four strangers from different walks of life. Shortlisted for the Booker Prize in 1996, this book is a gripping account of political, social and economic forces that drove India through the late 20th-century and into its current form.',
  url: 'https://img.theculturetrip.com/1440x/smart/wp-content/uploads/2017/08/rohinton-mistry-a-fine-balance-faber.jpg'
}];

